import './App.css';
import React from 'react';

function App() {

  const [Color, setColor] = React.useState('')

  const colorpattern = [
    {
      color: 'red',
      hexvalue: '#FF0000'
    },
    {
      color: 'green',
      hexvalue: '#008000'
    },
    {
      color: 'blue',
      hexvalue: '#0000FF'
    },
    {
      color: 'black',
      hexvalue: '#000000'
    },
    {
      color: 'white',
      hexvalue: '#FFFFFF'
    }
  ]
  const handleChange = (e) => {
    setColor(e.target.value)
  }

  const handleColor = (Color) => {
    console.log(colorpattern(Color))
  }
  return (
    <div className="App">
      <textarea

      />
      <input
        type = "text"
        value = {Color}
        placeholder = "Enter color"
        onChange={(e) => {handleChange(e)}}
      />
      <button onClick={(Color)=>handleColor(Color)}>Click Me!</button>
    </div>
  );
}

export default App;
