import React from 'react'

const Square = ({ colorValue }) => {
  return (
    <section className='square'
        style={{ backgroundColor: colorValue}}>

    <p>{colorValue ? colorValue : "Empty Value"}</p>
    </section>    
  )
}

Square.defaultProps = {
    colorValue: "Default Color Value"
}
export default Square
