  function handleUsers(e){
    console.log(e)
    setBtnSelect(e.target.value)
    console.log(btnselect)
  }
  function handlePosts(e){
    setBtnSelect(e.target.value)
  }
  function handleComments(e){
    setBtnSelect(e.target.value)
  }
  async function fetchItems(){
    try {
      const response = await fetch(`${API_URL}/${btnselect}`)
      if (response.status === 200) {
        const data = await response.json()
        setComments(data)
      } else {
        throw new Error ('Comments Data Load: Error Occured')
      }
    } catch (e) {
      setErrors(e.message)
    } finally {
      setBtnSelect(null)
    }
  }

  React.useEffect(() => {
    fetchItems()
  },[btnselect])
  return (
    <main className='main'>
        <ul>
            <li className='new'>
            <button value='users' onClick={(e)=>handleUsers(e)}>Users</button>
            <button value='posts' onClick={(e)=>handlePosts(e)}>Posts</button>
            <button value='comments' onClick={(e)=>handleComments(e)}>Comments</button>
        </li>
        </ul>
        {errors} ? (
          <p>{errors}</p>
        ) : (
              {users} ? (              
                  <Display  
                    users={users}
                  />  
              )
            )  


    </main>
  )
}

export default Header
