import React from 'react'
import Display from './Display.js'
import './Header.css'

function Header() {
  const API_URL = 'https://jsonplaceholder.typicode.com'
  const [btnselect, setBtnSelect] = React.useState(null)
  const [errors, setErrors] = React.useState([])
  const [users, setUsers] = React.useState([])
  const [posts, setPosts] = React.useState([])
  const [comments, setComments] = React.useState([])
  async function handleUsers(){
    setBtnSelect('Users')
    try {
      const response = await fetch(API_URL/btnselect)
      if (response.status === 200) {
        const data = await response.json()
        setUsers(data)
      } else {
        throw new Error ('Users Data Load: Error Occured')
      }
    } catch (e) {
      setErrors(e.message)
    } finally {
      setBtnSelect(null)
    }
  }  
  async function handlePosts(){
    setBtnSelect('Posts')
    try {
      const response = await fetch(API_URL/btnselect)
      if (response.status === 200) {
        const data = await response.json()
        setPosts(data)
      } else {
        throw new Error ('Posts Data Load: Error Occured')
      }
    } catch (e) {
      setErrors(e.message)
    } finally {
      setBtnSelect(null)
    }
  }
  async function handleComments(){
    setBtnSelect('Comments')
    try {
      const response = await fetch(API_URL/btnselect)
      if (response.status === 200) {
        const data = await response.json()
        setComments(data)
      } else {
        throw new Error ('Comments Data Load: Error Occured')
      }
    } catch (e) {
      setErrors(e.message)
    } finally {
      setBtnSelect(null)
    }
  }
  return (
    <main className='main'>
        <ul>
            <li className='new'>
            <button onClick={handleUsers}>Users</button>
            <button onclick={handlePosts}>Posts</button>
            <button onclick={handleComments}>Comments</button>
        </li>
        </ul>
    </main>
  )
}

export default Header
